const WIDTH = 640;
const HEIGHT = 480;
class Car{
  x = 50;
  y = 50;
};
export default function sketch(p){
  let canvas;
  let image;
  p.setup = () => {
    image = p.createImage(WIDTH, HEIGHT);
    canvas = p.createCanvas(WIDTH, HEIGHT);
    p.noStroke();
    p.background(0);
  }
  p.mousePressed = () => {
    console.log(p.get(p.mouseX, p.mouseY));
  }
  p.draw = () => {
    if (image){
      p.image(image,0,0,WIDTH,HEIGHT);
    }
  }
  p.myCustomRedrawAccordingToNewPropsHandler = (newProps) => {
    if(canvas)
      p.fill(newProps.color);
  }
  p.keyPressed = () => {
    if (p.keyCode == 76){
        const px = JSON.parse(window.localStorage.getItem('map'));
        const image1 = p.get(0, 0, WIDTH, HEIGHT);
        image1.loadPixels();
        for(var i = 0; i < image1.pixels.length; i += 4){
          const startIndex = Math.floor(i/4);
          image1.pixels[i] = px[startIndex][0];
          image1.pixels[i+1] = px[startIndex][1];
          image1.pixels[i+2] = px[startIndex][2];
      }
      image1.updatePixels();
      image=image1;
    }
  }
}
