import { Layer, Network } from 'synaptic';

class NNetwork {
  constructor() {
    var inputLayer = new Layer(1);
    var hiddenLayer = new Layer(5);
    var outputLayer = new Layer(4);
    inputLayer.project(hiddenLayer);
    hiddenLayer.project(outputLayer);
    this.nn = new Network({
      input: inputLayer,
      hidden: [hiddenLayer],
      output: outputLayer
    });
  }

}

const WIDTH = 640;
const HEIGHT = 480;

const getRadian = angle => angle * Math.PI / 180;

class DistanceSensor {
  constructor() {
    this.maxDistance = 100;
    this.foundX = 0;
    this.foundY = 0;

    this.findIntersection = (worldPixels, targetX, targetY, carX, carY, carAngle) => {
      const distance = Math.sqrt(Math.pow(carX - targetX, 2) + Math.pow(carY - targetY), 2);

      if (distance > this.maxDistance) {
        return false;
      }

      const pixel = worldPixels[Math.floor(carY) * WIDTH + Math.floor(carX)];

      if (pixel && pixel[0] !== 0) {
        const centerX = carX + (targetX - carX) / 2;
        const centerY = carY + (targetY - carY) / 2;
        return this.findIntersection(worldPixels, carX + 1 * Math.cos(getRadian(carAngle)), carY + 1 * Math.sin(getRadian(carAngle)), centerX, centerY, carAngle);
      }

      return [carX, carY];
    };

    this.getValue = (worldPixels, carX, carY, carAngle) => {
      const targetX = carX + this.maxDistance * Math.cos(getRadian(carAngle));
      const targetY = carY + this.maxDistance * Math.sin(getRadian(carAngle));

      if (worldPixels) {
        const result = this.findIntersection(worldPixels, carX, carY, targetX, targetY, carAngle);
        const [foundX, foundY] = result ? result : [targetX, targetY];
        this.foundX = foundX;
        this.foundY = foundY;
        return Math.min(1, Math.sqrt(Math.pow(carX - foundX, 2) + Math.pow(carY - foundY, 2)) / this.maxDistance);
      }

      return 0;
    };
  }

}

