const WIDTH = 800;
const HEIGHT = 600;
export default function sketch(p){
  let canvas;
  let image;
  p.setup = () => {
    image = p.createImage(WIDTH, HEIGHT);
    canvas = p.createCanvas(WIDTH, HEIGHT);
    p.noStroke();
    p.background(255);
  }
  p.draw = () => {
    p.fill('black');
    p.noStroke(0);
    if (p.mouseIsPressed === true) {
      p.circle(p.mouseX, p.mouseY,40);
    }
  }
  p.myCustomRedrawAccordingToNewPropsHandler = (newProps) => {
    if(canvas)
      p.fill(newProps.color);
  }
  p.keyPressed = () => {
    if (p.keyCode == 83){
      p.loadPixels();
      console.log(p.get());
    }
  }
}
