const WIDTH = 640;
const HEIGHT = 480;
class Car{
  x = 50;
  y = 50;
  height = 25;
  width = 40;
  speed = 0;
  maxSpeed = 3;
  acceleration = 0.5;
  angle = 0;
  speedUp = () => {
    this.speed = Math.min(this.maxSpeed, this.speed += this.acceleration);
  };
  speedDown = () => {
    this.speed = Math.max(0, this.speed -= this.acceleration);
  };
  steerLeft = () => {
    this.angle = (this.angle -1) % 360;
  }
  steerRight = () => {
    this.angle = (this.angle +1) % 360;
  }
  update = () => {
    this.x += this.speed;
    this.y += this.speed;
  };
};
export default function sketch(p){
  let canvas;
  let image;
  const car = new Car();

  p.setup = () => {
    image = p.createImage(WIDTH, HEIGHT);
    canvas = p.createCanvas(WIDTH, HEIGHT);
    p.noStroke();
    p.background(0);
  }
  p.mousePressed = () => {
    console.log(p.get(p.mouseX, p.mouseY));
  }
  p.draw = () => {
    p.background(255);
    p.update();
    if (image){
      p.image(image,0,0,WIDTH,HEIGHT);
    }
    p.translate(car.x, car.y);
    p.angleMode(p.DEGREES);
    p.rotate( car.angle);
    p.fill(150,100,200);
    p.rect(car.width / 2, car.height / 2, car.width, car.height);
    p.fill(150,150,150);
    p.rect(42,18,10,15);
    p.fill('black');
    p.rect(56,12,4,4);
    p.rect(56,34,4,4);
  }
  p.update = () => {
      if (p.keyIsDown(87)){
        car.speedUp();
      }
      else if (p.keyIsDown(83)){
        car.speedDown();
      }
      else if (p.keyIsDown(65)){
        car.steerLeft();
      }
      else if (p.keyIsDown(68)){
        car.steerRight();
      }
    car.update();
  };
  p.myCustomRedrawAccordingToNewPropsHandler = (newProps) => {
    if(canvas)
      p.fill(newProps.color);
  }
  p.keyPressed = () => {
    if(p.keyCode == 76){
        const px = JSON.parse(window.localStorage.getItem('map'));
        const image1 = p.get(0, 0, WIDTH, HEIGHT);
        image1.loadPixels();
        for(var i = 0; i < image1.pixels.length; i += 4){
          const startIndex = Math.floor(i/4);
          image1.pixels[i] = px[startIndex][0];
          image1.pixels[i+1] = px[startIndex][1];
          image1.pixels[i+2] = px[startIndex][2];
      }
      image1.updatePixels();
      image=image1;
    }
  }
}
